mapper.select:
select count(1) from ch_paper_innovate
	where 
		 deleted_flag = 0 and
		title=#{title,jdbcType=VARCHAR} 
		
			and id != #{id,jdbcType=INTEGER}

mapper.insert:
insert into ch_paper_innovate (id, title, type_id, 
  version_no, created_by_id, created_time, 
  last_updated_by_id, last_updated_time, deleted_flag
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{typeId,jdbcType=TINYINT}, 
  #{versionNo,jdbcType=INTEGER}, #{createdById,jdbcType=BIGINT}, #{createdTime,jdbcType=TIMESTAMP}, 
  #{lastUpdatedById,jdbcType=BIGINT}, #{lastUpdatedTime,jdbcType=TIMESTAMP}, #{deletedFlag,jdbcType=BIT}
  )
